'use strict'

import {default as browser} from 'open'

export function open(url){
	return new Promise((resolve, reject) => {
		if (!/https?: || file:/.test(url))
			reject('A valid http(s) or file url is required to open the browser')

		browser(url, (error, ...output) => {
			if (error) return reject(error)
			
			resolve(output)
		})
	})
}