'use strict'

var glob = require('glob')

export const 
	VIDEOS_FOLDER 								= process.argv[2] || __dirname
, SUPPORTED_VIDEO_FORMATS_GLOB 	= '**/*.{mp4,ogg,webm}'

export function lookup() {
	return new Promise((resolve, reject) => {
		new glob(SUPPORTED_VIDEO_FORMATS_GLOB, {cwd: VIDEOS_FOLDER, realpath: true}, (err, files) => {
			if (err)
				reject(err)
			else
				resolve(files)
		})
	})
}